function readURL(n){if(n.files&&n.files[0]){var t=new FileReader;t.onload=function(t){$(".image-upload-wrap").hide();$(".file-upload-image").attr("src",t.target.result);$(".file-upload-content").show();$(".image-title").html(n.files[0].name)};t.readAsDataURL(n.files[0])}else removeUpload()}function removeUpload(){$(".file-upload-input").replaceWith($(".file-upload-input").clone());$(".file-upload-content").hide();$(".image-upload-wrap").show()}$(".image-upload-wrap").bind("dragover",function(){$(".image-upload-wrap").addClass("image-dropping")});$(".image-upload-wrap").bind("dragleave",function(){$(".image-upload-wrap").removeClass("image-dropping")});